    public static boolean hasGroupsSizeX(int[] deck) {
        Map<Integer, Integer> count = new HashMap<>();
        for (int card : deck)
            count.put(card, count.getOrDefault(card, 0) + 1);
        int minX = 10001;
        for(int value : count.values()) {
            if (value == 1)
                return false;
            else if (value < minX)
                minX = value;
        }
        for (int i = 2; i <= minX; i++) {
            int countX = 0;
            for (int value : count.values()) {
                if (value % i == 0)
                    countX++;
                else
                    break;
            }
            if (countX == count.size())
                return true;
        }
        return false;
    }
